import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';


class App extends Component {
  state = {
    persons: [
      { name: 'Justin', age: '25'},
      { name: 'Giselle', age: '48'},
      { name: 'Donald', age: '78'}
    ],
    otherState: 'not part of the above array',
    showPersons: false
  }



  switchNamehandler = (newName) => {
    //console.log('Was Clicked');
    this.setState( {
      persons: [
        { name: newName, age: '25'},
        { name: 'Giselle', age: '2'},
        { name: 'Donald', age: '78'}
      ]
    })
  };

  nameChangedHandler = (event) => {
    this.setState( {
      persons: [
        { name: 'Manu', age: '25'},
        { name: event.target.value, age: '2'},
        { name: 'Donald', age: '78'}
      ]
    })
  }

  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow})
  }

  render(){
    return (
      <div className="App">
        <div> Hi i'm a React App! </div>
        <button
          onClick={this.togglePersonsHandler}> Click Me! </button>
        {
          this.state.showPersons == true ? 
            <div>
              <Person
              name = {this.state.persons[0].name}
              age = {this.state.persons[0].age}
              click={this.switchNamehandler.bind(this, 'Jarred')}> </Person>
              <Person
              name = {this.state.persons[1].name}
              age = {this.state.persons[1].age}
              changed = {this.nameChangedHandler}> </Person>
              <Person name = {this.state.persons[2].name}
              age = {this.state.persons[2].age}> </Person>
            </div> : null
        }
      </div>
    );
  };
}
export default App;
