import React, { Component } from "react";
import "../Styles/Main.scss";
import FadeInAnimation from "../Components/FadeInAnimation";
import QueueAnim from "rc-queue-anim";
import ScrollTrigger from "react-scroll-trigger";

class App extends Component {
  onEnterViewport() {
    this.setState({
      visible: true,
    });
  }
  onExitViewport() {
    this.setState({
      visible: false,
    });
  }

  render() {
    const { visible } = this.state;

    return (
      <section className="intro_section">
        <div className="introbox">
          <FadeInAnimation className="introbox_namelightleft" direction="left">
            Justin
          </FadeInAnimation>
          <FadeInAnimation
            className="introbox_namelightright"
            direction="right"
          >
            Poon
          </FadeInAnimation>
          <FadeInAnimation className="introbox_bio" direction="up">
            Welcome to my portfolio, I am a final year Civil Engineering student
            based in Melbourne, Australia. Explore this portfolio to find out
            more!
          </FadeInAnimation>
        </div>
        <div className="exp_section">
          <ScrollTrigger
            onEnter={this.onEnterViewport}
            onExit={this.onExitViewport}
          >
            <QueueAnim delay={300} className="queue-simple">
              <div key="a">Queue Demo</div>
              <div key="b">Queue Demo</div>
              <div key="c">Queue Demo</div>
              <div key="d">Queue Demo</div>
            </QueueAnim>
            <div className="exp_container"></div>
            <div className="exp_container"></div>
          </ScrollTrigger>
        </div>
      </section>
    );
  }
}
export default App;
