import React, { Component } from "react";
import "../Styles/Main.scss";
import FadeInAnimation from "../Components/FadeInAnimation";
import Zoom from "react-reveal/Zoom";

class App extends Component {
  state = {
    visible: false,
  };

  onEnterViewport = () => {
    this.setState({
      visible: true,
    });
  };
  onExitViewport = () => {
    this.setState({
      visible: false,
    });
  };

  render() {
    return (
      <body>
        <section className="intro_section">
          <div className="introbox">
            <FadeInAnimation
              className="introbox_namelightleft"
              direction="left"
              delay="0.5"
            >
              Justin
            </FadeInAnimation>
            <FadeInAnimation
              className="introbox_namelightright"
              direction="right"
            >
              Poon
            </FadeInAnimation>
            <FadeInAnimation
              className="introbox_bio"
              direction="up"
              delay="1.5"
            >
              Welcome to my portfolio, I am a final year Civil Engineering
              student based in Melbourne, Australia. Explore this portfolio to
              find out more!
            </FadeInAnimation>
          </div>
        </section>
        <section className="exp_section">
          <Zoom opposite="true">
            <div className="exp_container_1"></div>
          </Zoom>
        </section>
        <section className="exp_section">
          <Zoom opposite="true">
            <div className="exp_container"></div>
          </Zoom>
        </section>
      </body>
    );
  }
}
export default App;
