import React, { Component } from 'react';
import './App.css';
import Person from './Person/Person';


class App extends Component {
  state = {
    persons: [
      {id: 'asdfad', name: 'Giselle', age: '48'},
      {id: 'asdfae', name: 'Justin', age: '25'},
      {id: 'fadsfa', name: 'Donald', age: '78'}
    ],
    otherState: 'not part of the above array',
    showPersons: false
  }



  switchNamehandler = (newName) => {
    //console.log('Was Clicked');
    this.setState( {
      persons: [
        { name: newName, age: '25'},
        { name: 'Giselle', age: '2'},
        { name: 'Donald', age: '78'}
      ]
    })
  };

  nameChangedHandler = (event,id) => {
    const PersonIndex = this.state.persons.findIndex(p => {
      return p.id == id;
    });

    const person = {
      ...this.state.persons[PersonIndex]
    };

    person.name = event.target.name.value;

    const persons = [...this.state.persons];
    persons[PersonIndex] = person;

    this.setState( {
      persons: [
        { name: 'Manu', age: '25'},
        { name: event.target.value, age: '2'},
        { name: 'Donald', age: '78'}
      ]
    })
  }

  togglePersonsHandler = () => {
    const doesShow = this.state.showPersons;
    this.setState({showPersons: !doesShow})
  }

  deletePersonHandler = (PersonIndex) => {
    const persons = [...this.state.persons];
    persons.splice(PersonIndex, 1);
    this.setState({persons: persons})
  }

  render(){

    let persons = null;

    if (this.state.showPersons){
      persons = (
        <div>
        {this.state.persons.map((person, index) => {
          return <Person
            click = {() => this.deletePersonHandler(index)}
            name = {person.name}
            age = {person.age}
            key = {person.id}
            changed = {(event) => this.nameChangedHandler(event, person.id)} />
        })}
      </div>
      );
    }
    return (
      <div className="App">
        <div> Hi i'm a React App! </div>
        <button
          onClick={this.togglePersonsHandler}> Click Me! </button>
        {persons}
      </div>
    );
  };
}
export default App;
