import React, { Component } from "react";
import "../Styles/Main.scss";
import FadeInAnimation from "../Components/FadeInAnimation";
import QueueAnim from "rc-queue-anim";
import ScrollTrigger from "react-scroll-trigger";

class App extends Component {
  state = {
    visible: false,
  };

  onEnterViewport = () => {
    this.setState({
      visible: true,
    });
  };
  onExitViewport = () => {
    this.setState({
      visible: false,
    });
  };

  render() {
    const { visible } = this.state;

    return (
      <body>
        <section className="intro_section">
          <div className="introbox">
            <FadeInAnimation
              className="introbox_namelightleft"
              direction="left"
            >
              Justin
            </FadeInAnimation>
            <FadeInAnimation
              className="introbox_namelightright"
              direction="right"
            >
              Poon
            </FadeInAnimation>
            <FadeInAnimation className="introbox_bio" direction="up">
              Welcome to my portfolio, I am a final year Civil Engineering
              student based in Melbourne, Australia. Explore this portfolio to
              find out more!
            </FadeInAnimation>
          </div>
        </section>
        <section className="exp_section"></section>
      </body>
    );
  }
}
export default App;
